package Java;

public class switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String s="red";
		switch(s) {
		
		case "red":
			System.out.println("red");
			break;
		case "yellow":
			System.out.println("yellow");
			break;
		case "orange":
			System.out.println("orange");
			break;
		default:
			System.out.println("no color");
		}

	}

}
